document.addEventListener("DOMContentLoaded",(function(){console.log("تهيئة نظام إشعارات الخصم المنبثقة...");const discountPopup=document.getElementById("discountPopup");const discountPercentage=document.querySelector(".discount-popup-percentage");const addOutfitDiscountBtn=document.getElementById("addOutfitDiscountBtn");const addThirdOutfitBtn=document.getElementById("addThirdOutfitBtn");if(!discountPopup||!discountPercentage||!addOutfitDiscountBtn){console.error("لم يتم العثور على أحد عناصر DOM المطلوبة لإشعارات الخصم");return}function showDiscountPopup(percentage){discountPercentage.textContent=percentage+"%";discountPopup.classList.add("show");setTimeout((function(){discountPopup.classList.remove("show")}),2e3)}addOutfitDiscountBtn.addEventListener("click",(function(){console.log("تم الضغط على زر إضافة طقم آخر (خصم 10%)");showDiscountPopup(10)}));if(addThirdOutfitBtn){addThirdOutfitBtn.addEventListener("click",(function(){console.log("تم الضغط على زر إضافة الطقم الثالث (خصم 15%)");showDiscountPopup(15)}))}const addProductBtn=document.getElementById("addProductBtn");if(addProductBtn){addProductBtn.addEventListener("click",(function(){const currentItems=document.querySelectorAll(".product-item").length;if(currentItems===1){showDiscountPopup(10)}else if(currentItems===2){showDiscountPopup(15)}}))}console.log("تم تهيئة نظام إشعارات الخصم المنبثقة بنجاح")}));