function updateProductPreviewFixed(e,t){if(!e||!t)return;const c=e.querySelector(".product-preview-img");if(c){let e="";switch(t){case"black":case"blue":default:e="public/images/black/black-tshirt.jpg";break;case"beige":e="public/images/beige/beige-tshirt.jpg";break;case"silver":e="public/images/silver/silver-tshirt.jpg"}c.src=e}const i=e.querySelector(".product-preview-color");if(i){let e="";switch(t){case"black":default:e="أسود";break;case"beige":e="بيج";break;case"silver":e="فضي"}i.textContent=`اللون: ${e}`}}document.addEventListener("DOMContentLoaded",(function(){document.querySelectorAll(".product-color-option").forEach((e=>{e.addEventListener("click",(function(){updateProductPreviewFixed(this.closest(".product-item"),this.getAttribute("data-color"))}))}))}));